unsigned int gmp_itag(unsigned int value)
{
	unsigned int value2;
	char *s1 = (char *) &value;
	char *s2 = (char *) &value2;
	/* return if value is ascii code */
	if (s1[0] >= 0x20)
		return value;
	/* convert integer to ascii code */
	s2[0] = s1[3];
	s2[1] = s1[2];
	s2[2] = s1[1];
	s2[3] = s1[0];
	if (s2[0] < 0x30)
		s2[0] += 0x30;
	if (s2[1] < 0x30)
		s2[1] += 0x30;
	if (s2[2] < 0x30)
		s2[2] += 0x30;
	if (s2[3] < 0x30)
		s2[3] += 0x30;
	return value2;
}

unsigned int gmp_ctag(char a, char b, char c, char d)
{
	unsigned int value;
	char *s = (char *) &value;
	s[0] = a;
	s[1] = b;
	s[2] = c;
	s[3] = d;
	return value;
}

