{
	struct gmdesc *p;

	gmp_trace();

	p = gmp->cached;

	if (p && p->pa == pa)
		return p->secure_id;

	p = gmp->gmdesc;

	while (p) {
		if (p->pa == pa)
			break;
		p = next_gmdesc(gmp, p);
	}

	if (p)
		return p->secure_id;

	return (unsigned int) -1;
}

