	(*geinfo)->fd = fd;

	ioctl(fd, FBIOGET_FSCREENINFO, &fb_fix);

	len = fb_fix.smem_len;
	off = (len + 0x0fff) & ~0x0fff;
	p = (unsigned long)mmap(0, fb_fix.mmio_len, PROT_READ|PROT_WRITE,
		MAP_SHARED, fd, off);
	p += GE_MMIO_OFFSET;
	(*geinfo)->mmio = (void *)p;

