		regs->src_hm_sel = 2;      /* 454 */
		regs->src_input_sel = 0;
		break;
	case GSPF_RGB32:
	case GSPF_ARGB:
		regs->src_yuv_en = 0;
		regs->src_color_depth = 3; /* 32 bpp */
		regs->src_input_sel = 0;
		break;
	case GSPF_YUV420SP:
		ge_set_src_csc(geinfo, GE_CSC_YC_RGB_JFIF_0_255);
		regs->src_yuv_en = 1;
		regs->src_fmt444 = 0;
		regs->src_vfmt = 1;
		regs->src_csc_en = 1;
		regs->src_color_depth = 3; /* 32 bpp */
		regs->src_input_sel = 0;
		break;
	case GSPF_YUV422SP:
